import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons"
import styled from "styled-components"

const Container =  styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
    overflow: hidden;

`
const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #fff7f7;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`
const Wrapper = styled.div`
    height: 100%;
    display: flex;
`
const Slide = styled.div`
    display: flex;
    align-items: center;
    width: 100vw; //contain the entire Wrapper
    height: 100vh;
`
const ImageContainer = styled.div`
    flex: 1;
    height: 100%
`
const Image = styled.img`
    height: 80%;
`
const InfoContainer = styled.div`
    flex: 1;
    padding: 50px;
`
const Title = styled.h1`
    font-size: 70px;
`
const Desc = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weight: 500;
    letter-spacing: 3px;
`
const Button = styled.button`
    padding: 10px;
    font-size: 20px;
    background-color: transparent;
    cursor: pointer;
`
const Slider = () => {
  return (
    <Container>
        <Arrow direction="left">
              <ArrowLeftOutlined/>
        </Arrow>
        <Wrapper>
          <Slide>
            <ImageContainer>
            <Image src="https://i.ibb.co/DG69bQ4/2.png"/>
            </ImageContainer>
            <InfoContainer>
              <Title>BEDROOM DECOR</Title>
              <Desc>Don't Compromise on Sytle! Get flat 30% off for new arrivals!</Desc>
              <Button>Shop Now</Button>
            </InfoContainer>
          </Slide>
        <Slide>
          <ImageContainer>
            <Image src="https://i.ibb.co/DG69bQ4/2.png" />
          </ImageContainer>
          <InfoContainer>
            <Title>KITCHEN CLLECTION</Title>
            <Desc>Don't Compromise on Sytle! Get flat 30% off for new arrivals!</Desc>
            <Button>Shop Now</Button>
          </InfoContainer>
        </Slide>
        <Slide>
          <ImageContainer>
            <Image src="https://i.ibb.co/DG69bQ4/2.png" />
          </ImageContainer>
          <InfoContainer>
            <Title>SYTLE YOUR WARDRBOE</Title>
            <Desc>Don't Compromise on Sytle! Get flat 30% off for new arrivals!</Desc>
            <Button>Shop Now</Button>
          </InfoContainer>
        </Slide>
        </Wrapper>

        <Arrow direction="right">
            <ArrowRightOutlined />
        </Arrow>
    </Container>
  )
}

export default Slider
